mapper.insert:
INSERT 
  INTO COMMONCODETYPE
VALUES (
	#{CODETYPE_ID}
    , #{CODETYPE_NAME}
    , #{DESCRIPTION}
    , 1						-- 1: 사용, 2: 미사용
)

mapper.select:
SELECT M.CODETYPE_ID , M.CODETYPE_NAME , M.DESCRIPTION AS MAIN_DESC , S.CODE_ID , S.CODE_NAME , S.DESCRIPTION AS SUB_DESC , S.CODE_STATUS , S.CODE_SEQ FROM COMMONCODETYPE M JOIN COMMONCODE S ON M.CODETYPE_ID = S.CODETYPE_ID WHERE 1 = 1
AND M.CODETYPE_NAME LIKE CONCAT('%', #{searchValue}, '%') OR M.CODETYPE_ID LIKE CONCAT('%', #{searchValue}, '%') OR M.DESCRIPTION LIKE CONCAT('%', #{searchValue}, '%') OR S.CODE_ID LIKE CONCAT('%', #{searchValue}, '%') OR S.CODE_NAME LIKE CONCAT('%', #{searchValue}, '%') OR S.DESCRIPTION LIKE CONCAT('%', #{searchValue}, '%')
LIMIT #{start} , #{length}

mapper.update:
     	UPDATE COMMONCODETYPE M, COMMONCODE S
		   SET 
		    	M.CODETYPE_NAME = #{CODETYPE_NAME}
			    , M.DESCRIPTION = #{CODETYPE_DESC}
			    , S.CODE_ID = #{CODE_ID}
			    , S.CODE_NAME = #{CODE_NAME}
			    , S.DESCRIPTION = #{CODE_DESC}
			    , S.CODE_STATUS = #{CODE_STATUS}
			    , S.CODE_SEQ = #{CODE_SEQ}
		 WHERE
				M.CODETYPE_ID = #{OLD_CODETYPE_ID}
			    AND S.CODETYPE_ID = #{OLD_CODETYPE_ID}
			    AND S.CODE_ID = #{OLD_CODE_ID}

mapper.delete:
DELETE FROM COMMONCODE
 WHERE CODETYPE_ID = #{CODETYPE_ID}
   AND CODE_ID = #{CODE_ID}
