mapper.insert:
INSERT 
  INTO COMMONCODETYPE
VALUES (
	#{CODETYPE_ID}
    , #{CODETYPE_NAME}
    , #{DESCRIPTION}
    , 1						-- 1: 사용, 2: 미사용
)

mapper.update:
     	UPDATE COMMONCODETYPE M, COMMONCODE S
		   SET 
		    	M.CODETYPE_NAME = #{CODETYPE_NAME}
			    , M.DESCRIPTION = #{CODETYPE_DESC}
			    , S.CODE_ID = #{CODE_ID}
			    , S.CODE_NAME = #{CODE_NAME}
			    , S.DESCRIPTION = #{CODE_DESC}
			    , S.CODE_STATUS = #{CODE_STATUS}
			    , S.CODE_SEQ = #{CODE_SEQ}
		 WHERE
				M.CODETYPE_ID = #{OLD_CODETYPE_ID}
			    AND S.CODETYPE_ID = #{OLD_CODETYPE_ID}
			    AND S.CODE_ID = #{OLD_CODE_ID}

mapper.delete:
DELETE FROM COMMONCODE
 WHERE CODETYPE_ID = #{CODETYPE_ID}
   AND CODE_ID = #{CODE_ID}

mapper.select:
    	SELECT "TRUE" AS RESULT
		  FROM COMMONCODETYPE
		 WHERE CODETYPE_ID = #{mainCode};

     	SELECT "TRUE" AS RESULT
		  FROM COMMONCODE
		 WHERE CODE_ID = #{subCode}

SELECT MEM_ID , MEM_NAME , MEM_NICK , MEM_BIRTHDAY , MEM_EMAIL , (CASE WHEN MEM_GENDER = "M" THEN "남" WHEN MEM_GENDER = "F" THEN "여" END) AS MEM_GENDER , (CASE WHEN MEM_PHONE IS NULL OR MEM_PHONE = '' THEN '' ELSE CONCAT(SUBSTR(MEM_PHONE, 1, 3), '-', SUBSTR(MEM_PHONE, 4, 4), '-', SUBSTR(MEM_PHONE, 8, 4)) END) AS MEM_PHONE , MEM_POST_CODE , MEM_ADDRESS1 , MEM_ADDRESS2 , MEM_GRADE , MEM_STATUS , MEM_REG_DATE , MEM_WITHDRAW_DATE , MEM_PROFILE , MEM_INTRO , SNS_STATUS , AUTH_STATUS , REPORT_CNT FROM MEMBER WHERE 1 = 1
AND MEM_STATUS = #{param.mem_status}
AND MEM_GRADE = #{param.mem_grade}
AND (MEM_ID LIKE CONCAT('%', #{param.searchValue}, '%') OR MEM_NAME LIKE CONCAT('%', #{param.searchValue}, '%') OR MEM_NICK LIKE CONCAT('%', #{param.searchValue}, '%') OR MEM_EMAIL LIKE CONCAT('%', #{param.searchValue}, '%'))
ORDER BY
MEM_ID MEM_NAME MEM_NICK MEM_EMAIL MEM_REG_DATE MEM_REG_DATE
ASC DESC LIMIT #{param.start} , #{param.length}

SELECT COUNT(*) FROM MEMBER WHERE 1 = 1
AND MEM_STATUS = #{param.mem_status}
AND MEM_GRADE = #{param.mem_grade}
AND (MEM_ID LIKE CONCAT('%', #{param.searchValue}, '%') OR MEM_NAME LIKE CONCAT('%', #{param.searchValue}, '%') OR MEM_NICK LIKE CONCAT('%', #{param.searchValue}, '%') OR MEM_EMAIL LIKE CONCAT('%', #{param.searchValue}, '%'))

mapper.update:
UPDATE MEMBER
   SET MEM_GRADE = #{mem_grade}
   	   , MEM_STATUS = #{mem_status}
 WHERE MEM_ID = #{mem_id}

     	UPDATE PRODUCT_REPORTS A LEFT JOIN PRODUCT B
     	    ON A.PRODUCT_ID = B.PRODUCT_ID
		
			   A.STATUS = #{STATUS}
			   , A.ACTION_REASON = #{ACTION_REASON}
			   , A.ADMIN_ID = #{ADMIN_ID}
			   , A.ACTION_DATE = NOW()
			
     	   	   , B.PRODUCT_STATUS = 4
			
			
     	   	   , B.PRODUCT_STATUS = 0
			
		
     	 WHERE A.REPORT_ID = CAST(#{REPORT_ID} AS UNSIGNED)
		
     	   AND B.PRODUCT_ID = CAST(#{PRODUCT_ID} AS UNSIGNED)

     	UPDATE USER_REPORTS
		   SET STATUS = #{STATUS}
				, ACTION_REASON = #{ACTION_REASON}
				, ADMIN_ID = #{ADMIN_ID}
				, ACTION_DATE = NOW()
		 WHERE REPORT_ID = #{REPORT_ID}
		   AND ROOM_ID = #{ROOM_ID}
		   AND REPORTED_ID = #{REPORTED_ID}

mapper.select:
SELECT * FROM FAQ WHERE 1 = 1
AND FAQ_CATE = #{param.faq_cate}
AND LIST_STATUS = #{param.list_status}
AND FAQ_SUBJECT LIKE CONCAT('%', #{param.searchValue}, '%') OR FAQ_CONTENT LIKE CONCAT('%', #{param.searchValue}, '%')
ORDER BY
FAQ_ID FAQ_SUBJECT FAQ_CONTENT FAQ_CATE FAQ_CATE
ASC DESC LIMIT #{param.start} , #{param.length}

mapper.update:
UPDATE FAQ
   SET FAQ_SUBJECT = #{faq_subject}
   	   , FAQ_CONTENT = #{faq_content}
   	   , FAQ_CATE = #{faq_cate}
   	   , LIST_STATUS = #{list_status}
 WHERE FAQ_ID = #{faq_id}

     	UPDATE FAQ
		   SET 
			    LIST_STATUS = #{param.LIST_STATUS}
		 WHERE
		 	FAQ_ID = #{param.FAQ_ID}

     	UPDATE SUPPORT
		
			   STATUS = #{STATUS}
			   , REPLY_CONTENT = #{REPLY_CONTENT}
			   , REPLY_DATE = CURRENT_TIMESTAMP

		
		WHERE SUPPORT_ID = #{SUPPORT_ID}

mapper.select:
    	SELECT DATE(PRODUCT_REG_DATE) AS date
				, COUNT(*) AS productTotal
		FROM PRODUCT
		WHERE DATE(PRODUCT_REG_DATE) BETWEEN #{startDate} AND #{endDate}
    	GROUP BY DATE(PRODUCT_REG_DATE)
    	ORDER BY DATE ${orderDir}
