mapper.insert:
INSERT 
  INTO COMMONCODETYPE
VALUES (
	#{CODETYPE_ID}
    , #{CODETYPE_NAME}
    , #{DESCRIPTION}
    , 1						-- 1: 사용, 2: 미사용
)

mapper.update:
     	UPDATE COMMONCODETYPE M, COMMONCODE S
		   SET 
		    	M.CODETYPE_NAME = #{CODETYPE_NAME}
			    , M.DESCRIPTION = #{CODETYPE_DESC}
			    , S.CODE_ID = #{CODE_ID}
			    , S.CODE_NAME = #{CODE_NAME}
			    , S.DESCRIPTION = #{CODE_DESC}
			    , S.CODE_STATUS = #{CODE_STATUS}
			    , S.CODE_SEQ = #{CODE_SEQ}
		 WHERE
				M.CODETYPE_ID = #{OLD_CODETYPE_ID}
			    AND S.CODETYPE_ID = #{OLD_CODETYPE_ID}
			    AND S.CODE_ID = #{OLD_CODE_ID}

mapper.delete:
DELETE FROM COMMONCODE
 WHERE CODETYPE_ID = #{CODETYPE_ID}
   AND CODE_ID = #{CODE_ID}

mapper.select:
SELECT MEM_ID , MEM_NAME , MEM_NICK , MEM_BIRTHDAY , MEM_EMAIL , (CASE WHEN MEM_GENDER = "M" THEN "남" WHEN MEM_GENDER = "F" THEN "여" END) AS MEM_GENDER , (CASE WHEN MEM_PHONE IS NULL OR MEM_PHONE = '' THEN '' ELSE CONCAT(SUBSTR(MEM_PHONE, 1, 3), '-', SUBSTR(MEM_PHONE, 4, 4), '-', SUBSTR(MEM_PHONE, 8, 4)) END) AS MEM_PHONE , MEM_POST_CODE , MEM_ADDRESS1 , MEM_ADDRESS2 , MEM_GRADE , MEM_STATUS , MEM_REG_DATE , MEM_WITHDRAW_DATE , MEM_PROFILE , MEM_INTRO , SNS_STATUS , AUTH_STATUS FROM MEMBER WHERE 1 = 1
AND MEM_STATUS = #{memStatus}
AND MEM_GRADE = #{memGrade}
AND (MEM_ID LIKE CONCAT('%', #{searchValue}, '%') OR MEM_NAME LIKE CONCAT('%', #{searchValue}, '%') OR MEM_NICK LIKE CONCAT('%', #{searchValue}, '%') OR MEM_EMAIL LIKE CONCAT('%', #{searchValue}, '%'))
ORDER BY
MEM_ID MEM_NAME MEM_NICK MEM_EMAIL MEM_REG_DATE MEM_REG_DATE
ASC DESC LIMIT #{start} , #{length}

SELECT COUNT(*) FROM MEMBER WHERE 1 = 1
AND MEM_STATUS = #{memStatus}
AND MEM_GRADE = #{memGrade}
AND (MEM_ID LIKE CONCAT('%', #{searchValue}, '%') OR MEM_NAME LIKE CONCAT('%', #{searchValue}, '%') OR MEM_NICK LIKE CONCAT('%', #{searchValue}, '%') OR MEM_EMAIL LIKE CONCAT('%', #{searchValue}, '%'))

mapper.update:
UPDATE MEMBER
   SET MEM_GRADE = #{mem_grade}
   	   , MEM_STATUS = #{mem_status}
 WHERE MEM_ID = #{mem_id}

mapper.select:
SELECT FAQ_ID ,FAQ_SUBJECT , FAQ_CONTENT , FAQ_CATE FROM FAQ WHERE 1 = 1
AND FAQ_SUBJECT LIKE CONCAT('%', #{searchValue}, '%') OR FAQ_CONTENT LIKE CONCAT('%', #{searchValue}, '%')
LIMIT #{start} , #{length}
