mapper.insert:

SELECT IFNULL(MAX(FAQ_ID), 0)
  FROM FAQ

INSERT
  INTO FAQ
VALUES (
	   #{faq_id} + 1
	 , #{faq_subject}
	 , #{faq_content}
	 , #{faq_cate}
	 , 1	-- 기본값 1등록
	 )

mapper.update:
UPDATE FAQ
SET 
	MEM_NICK = #{mem_nick}


		, MEM_PASSWD = #{mem_passwd}

	, MEM_POST_CODE = #{mem_post_code}
	, MEM_ADDRESS1 = #{mem_address1}
	, MEM_ADDRESS2 = #{mem_address2}
	, MEM_PROFILE = #{mem_profile}


WHERE
	FAQ_ID = #{faq_id}
