mapper.select:
SELECT COUNT(*)
FROM PRODUCT P JOIN MYREVIEW R ON P.PRODUCT_ID = R.PRODUCT_ID
WHERE P.MEM_ID = #{mem_id};

mapper.insert:
		

			SELECT COALESCE(MAX(REVIEW_ID), 0) + 1
			FROM MYREVIEW;
		
		INSERT INTO MYREVIEW (
		    REVIEW_ID
		   , MEM_ID
		   , REVIEW_CONTENT
		   , REVIEW_DATE
		   , PRODUCT_TITLE
		   , REVIEW_SCORE
		   , PRODUCT_ID
		   , REVIEW_OPTIONS 
		) VALUES (
		    #{review_id}             -- 리뷰 ID
		    , #{mem_id}             -- 회원 ID
		    , #{review}  	 -- 리뷰 내용
		    , now()	 -- 리뷰 날짜
		    , #{product_title}
		    , #{score}
		    , #{product_id}            
		    , #{reviewOptions}
		);

mapper.update:
UPDATE MYREVIEW
SET REVIEW_CONTENT = #{review_content}
WHERE PRODUCT_ID = #{product_id}
