mapper.insert:
	
		SELECT IFNULL(MAX(SUPPORT_ID), 0)
		  FROM SUPPORT
	
	INSERT INTO SUPPORT
	VALUES (
		#{support_id}+1
		,#{support_category}
		,now()
		,#{mem_id}
		,#{support_subject}
		,#{support_content}
		,#{support_file1}
		,null
		,null
		,'접수'
	)

mapper.update:
UPDATE SUPPORT
SET SUPPORT_SUBJECT = #{support_subject}
	, SUPPORT_CONTENT = #{support_content}
WHERE SUPPORT_ID = #{support_id}
